    func getExclusiveGames() async throws -> [GamesModel] {
        return [
            GamesModel(name: "BUILD YOUR AQUARIUM", descrip: "Build Your Aquarium is a popular Sea World fishing game for players! You run an aquarium with customers with different needs. You need to keep going to the bottom of the sea to fish, collect all kinds of fish, unlock new tanks, expand your operation and your customers will grow! Become the richest aquarium tycoon! Join us now!", image: "buildYourAquarium", url: "https://html5.gamedistribution.com/55eaa28d7a6343f8951b8709aae7145d/?gd_sdk_referrer_url=https://gamedistribution.com/games/build-your-aquarium"),
            GamesModel(name: "MYSTIC MAHJONGG", descrip: "Embark on a captivating journey into the world of Mystic Mahjongg! Solve challenging puzzles by matching pairs of themed tiles adorned with intricate symbols! Сhallenge yourself with increasingly difficult levels, create a unique atmosphere by choosing new avatars and backgrounds. Immerse yourself in the magical world of Mystic Mahjongg - a new relaxing game with stunning visuals - explore all levels and uncover the magic hidden within the tiles! Begin your journey today with Mystic Mahjongg!", image: "mysticMahjongg", url: "https://html5.gamedistribution.com/fdedbc8bb114409b84e1d1ff853291ee/?gd_sdk_referrer_url=https://gamedistribution.com/games/mystic-mahjongg"),
            GamesModel(name: "FREE THE BALL", descrip: "Try out Free the Ball, a super fun puzzle game! Your job is to guide the ball to its goal by moving blocks around. And get this – there are a whopping three thousand levels to play, and they're all free! No need to buy anything.", image: "freetheBall", url: "https://html5.gamedistribution.com/f7364610976f43d1b31f730d9995641a/?gd_sdk_referrer_url=https://gamedistribution.com/games/free-the-ball-1"),
            GamesModel(name: "HIGH SPEED CRAZY BIKE", descrip: "Get ready to take on the ultimate mud dirt bike racing challenge as you barrel through rough terrain at breakneck speeds, using your skills and quick reflexes to navigate through a myriad of obstacles and emerge victorious in this adrenaline-fueled test of skill and daring.", image: "highBike", url: "https://html5.gamedistribution.com/e804dc59653849d5b24fa5919662c2af/?gd_sdk_referrer_url=https://gamedistribution.com/games/high-speed-bike-simulator"),
            GamesModel(name: "BIKE STUNT RACE MASTER 3D RACING", descrip: "In this bike game Tracks are arranged according to real bike race scenarios which carry multiple levels from easy trials to technical approach. Are you motorcycle racer?? Let’s ﬁnd out what you have, use your motorcycle driving tricks and ﬁnish all the challenging task.", image: "bikeStuntRacing", url: "https://html5.gamedistribution.com/a81c87f3a54f436eb152eb8654a6b254/?gd_sdk_referrer_url=https://gamedistribution.com/games/bike-stunt-race-master-3d-racing-1"),
        ]
    }
    
    func getNonoGames() async throws -> [GamesModel] {
        return [
            GamesModel(name: "FOR HONOR WARRIORS IO", descrip: "Fight your way to becoming the greates warrior of all time! Engage in a merciless free-for-all-war, Grow and fight with huge weapons, and unlock 12 FOR HONOR heroes! Fight endlessly and defeat hords of other players to become the strongest warrior ever. One hit Kills...stay focused!", image: "forHonorWarriors", url: "https://html5.gamedistribution.com/c23c547669904e348202fbba7f5f8306/?gd_sdk_referrer_url=https://gamedistribution.com/games/for-honor-warriors-io"),
            GamesModel(name: "BUBBLES & HUNGRY DRAGON", descrip: "Join a hilarious battle of dragons! ​Pick-up your mighty dragon and burst as many bubbles as possible! ​Aim, shoot and match bubbles of the same color to defeat other players in classic arcade team matches!​​", image: "bubblesHungryDragon", url: "https://html5.gamedistribution.com/136ce9a952c54adf93ef9dbfcc663904/?gd_sdk_referrer_url=https://gamedistribution.com/games/bubbles-and-hungry-dragon"),
            GamesModel(name: "TOM CLANCY'S SHOOTOUT", descrip: "Enter the most intense Training Ground! Show your mastery of weapons by shooting targets with reflex and precision. Shoot the drones before other players to unlock better weapons. Select an elite operator and get the world's best score!", image: "tomClancy", url: "https://html5.gamedistribution.com/6ebe84c1df814143978118cf948329ce/?gd_sdk_referrer_url=https://gamedistribution.com/games/tom-clancy's-shootout"),
            GamesModel(name: "RABBIDS VOLCANO PANIC", descrip: "Play Rabbids Volcano Panic on nano.ubisoft.com. Let us know what do you think about the game in the comments Rabbids island is crumbling in a colossal volcanic eruption! Stay safe as long as you can and make your way on the collapsing floor before the other panicking Rabbids take you down with them! Pick-up bonus items and trap other players to play dirty and make them fall first!", image: "rabbidsVolcano", url: "https://html5.gamedistribution.com/c8608aa5e1ee4f61967844ba20a279e9/?gd_sdk_referrer_url=https://gamedistribution.com/games/rabbids-volcano-panic"),
            GamesModel(name: "ASSASSIN'S CREED FREERUNNERS", descrip: "Try Assassin's Creed Freerunners on nano.ubisoft.com. It’s the ultimate Assassin freerunning test! Appoint your most trusted Assassin and enter the animus with 7 other players, surpass the dangers and get to the extraction point first! Climb on roofs, jump on walls, use the environment and take an epic leap of faith to increase your synchronization and summon legendary characters from the entire Assassin’s Creed series. Initiating simulation…3…2…1..Run!", image: "assassinCreed", url: "https://html5.gamedistribution.com/b5e69d9778554b0e9cb0a6d5d6a0e43d/?gd_sdk_referrer_url=https://gamedistribution.com/games/assassin's-creed-freerunners"),
        ]
